ource(paste0(dossier.codes, &#39;dyad_classif.R&#39;)) # uses model for pair trawlers to classify the dyads from the other fleets and produce graphs These functions are here. 2.2 Gaussian mixture modeling # load(paste0(dossier.data.outputs, &quot;all_dyads.RData&quot;)) # dyads and metrics dyads &lt;- readRDS(file = paste0(dossier.data.outputs,&quot;all_dyads_vJ.rds&quot;)) # dyads and metrics variables &lt;- c(&quot;Prox&quot;, &quot;DI.theta&quot;, &quot;DI.d&quot;) gear &lt;- &quot;all&quot; # just PTM and variables for analysis dyads %&gt;% filter(gear.1 == &quot;PTM&quot;) %&gt;% select(variables) -&gt; dta ##################### GMM estimation #################################### # specifying some parameters for the GMM parameter estimation strat &lt;- mixmodStrategy(algo = &quot;EM&quot;, initMethod = &quot;smallEM&quot;) # Calculate the number of cores free_cores &lt;- 2 no_cores &lt;- detectCores() - free_cores # leave 4 free # Initiate cluster cl &lt;- makeCluster(no_cores, type = &quot;FORK&quot;) # Then parallel GMM with the 3 metrics, for 3 clusters N_rep &lt;- 30 # number of replicates test &lt;- parLapply(cl, 1:N_rep, # 20 replicates function(x) { res1 &lt;- mixmodCluster( data = as.data.frame(dta[, 1:3]), nbCluster = 3, strategy = strat, criterion = c(&quot;ICL&quot;), models = mixmodGaussianModel( family = &quot;general&quot;, free.proportions = TRUE, equal.proportions = FALSE, listModels = c(&quot;Gaussian_pk_Lk_Ck&quot;) ) ) return(res1) }) stopCluster(cl) # choosing the best model from the replicates based on ICL BestModel &lt;- test[[order(rapply(test, function(x) x@bestResult@criterionValue))[2]]] # saving the best model saveRDS( BestModel, file = paste0( dossier.stats.outputs, &#39;BestFreeModel-PTM-&#39;, gear, &#39;-&#39;, paste0(variables, collapse = &#39;-&#39;), &#39;.rds&#39; ) ) ##################### Extracting output stats #################################### # now we are extracting variance values to compare the variance between clusters var_par &lt;- BestModel@bestResult@parameters@variance # var_par is a list with a number of elements equal to the number of clusters, and each element is a variance matrix for the metrics (for a given cluster) # we&#39;ll pay attention to the variances and not covariances, so we&#39;ll use the list to make a matrix where each column is a metric and each row is a cluster, and each element ij is the variance of the metric j in cluster i. variances &lt;- t(sapply(BestModel@bestResult@parameters@variance, function(mat_) { diag(mat_) })) # we then get the minimum variance per metric accross clusters. And, now that I think about it, this is biased by the fact that not all variables are in the same range. DI.theta goes from -1 to 1. minimal_var &lt;- apply(variances, 2, min) # now, how much is variance of each metric in each cluster is higher than the minimum variance of each metric throughout clusters? ratio_var &lt;- sweep(variances, 2, STATS = minimal_var, FUN = &#39;/&#39;) # another form of comparison can be done by using the variances (not covariances) and means to calculate the coefficients of variation cv &lt;- sqrt(t( sapply(BestModel@bestResult@parameters@variance, function(mat_) { diag(mat_) }) )) / BestModel@bestResult@parameters@mean # saving these results cluster.file = paste0( dossier.stats.outputs, &#39;cluster_var_PTM_&#39;, gear, &#39;-&#39;, paste(str_replace_all(variables, &quot;[[:punct:]]&quot;, &quot;&quot;), collapse = &#39;-&#39;), &#39;.txt&#39; ) sink(cluster.file) cat(&quot;Number of clusters: &quot;, BestModel@bestResult@nbCluster, &quot;
